import React, { useState, useRef, useEffect } from "react";
import axios from "axios";

const App = () => {
  const [gestureStarted, setGestureStarted] = useState(false);
  const [isVideoActive, setIsVideoActive] = useState(false);
  const videoRef = useRef(null);

  useEffect(() => {
    const startVideo = async () => {
      if (isVideoActive) {
        try {
          const stream = await navigator.mediaDevices.getUserMedia({ video: true });
          videoRef.current.srcObject = stream;
          videoRef.current.play();
        } catch (error) {
          console.error("Error accessing the camera:", error);
          alert("Permission to access the camera denied or unavailable.");
          setIsVideoActive(false); // Reset video state if permission is denied
        }
      } else {
        if (videoRef.current && videoRef.current.srcObject) {
          const tracks = videoRef.current.srcObject.getTracks();
          tracks.forEach((track) => track.stop());
          videoRef.current.srcObject = null;
        }
      }
    };

    startVideo();
  }, [isVideoActive]);

  const handleStartGesture = async () => {
    console.log("Start Button Clicked");
    try {
      const response = await axios.get("http://127.0.0.1:5000/start");
      if (response.data.status === "success") {
        alert("Gesture detection started successfully.");
        setGestureStarted(true);
      } else {
        alert("Failed to start gesture detection.");
      }
    } catch (error) {
      console.error("Error starting gesture detection:", error);
      alert("Unable to connect to the server. Ensure it is running.");
    }
  };

  const handleStopGesture = async () => {
    console.log("Stop Button Clicked");
    try {
      const response = await axios.get("http://127.0.0.1:5000/stop");
      if (response.data.status === "success") {
        alert("Gesture detection stopped successfully.");
        setGestureStarted(false);
      } else {
        alert("Failed to stop gesture detection.");
      }
    } catch (error) {
      console.error("Error stopping gesture detection:", error);
      alert("Unable to connect to the server. Ensure it is running.");
    }
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>Gesture Media Control</h1>
      <div
        style={{
          width: "100%",
          maxWidth: "600px",
          margin: "20px auto",
          border: "2px solid white",
          borderRadius: "10px",
          overflow: "hidden",
          backgroundColor: "black",
        }}
      >
        <video
          ref={videoRef}
          style={{
            width: "100%",
            height: "300px",
            display: isVideoActive ? "block" : "none",
          }}
        ></video>
      </div>
      <div>
        <button
          onClick={() => {
            setIsVideoActive(true);
            handleStartGesture();
          }}
          style={{
            padding: "10px 20px",
            margin: "10px",
            backgroundColor: "#4CAF50",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Start Gesture Detection
        </button>
        <button
          onClick={() => {
            setIsVideoActive(false);
            handleStopGesture();
          }}
          style={{
            padding: "10px 20px",
            margin: "10px",
            backgroundColor: "#f44336",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Stop Gesture Detection
        </button>
      </div>
    </div>
  );
};

export default App;
