import React, { useState, useEffect } from "react";
import axios from "axios";
const App = () => {
  const [gestureStarted, setGestureStarted] = useState(false);

  useEffect(() => {
    const canvas = document.createElement('canvas');
    const backgroundElement = document.getElementById('background');

    if (backgroundElement) {
      backgroundElement.appendChild(canvas); // Append canvas only if backgroundElement exists
      const ctx = canvas.getContext('2d');
      const particles = [];

      // Define resizeCanvas function inside useEffect
      const resizeCanvas = () => {
        canvas.width = window.innerWidth;
        canvas.height = window.innerHeight;
      };

      resizeCanvas(); // Call resizeCanvas initially
      window.addEventListener('resize', resizeCanvas); // Add event listener for window resize

      function Particle(x, y) {
        this.x = x;
        this.y = y;
        this.size = Math.random() * 3 + 1;
        this.speedX = Math.random() * 3 - 1.5;
        this.speedY = Math.random() * 3 - 1.5;
      }

      Particle.prototype.update = function() {
        this.x += this.speedX;
        this.y += this.speedY;
        if (this.size > 0.2) this.size -= 0.1;
      };

      Particle.prototype.draw = function() {
        ctx.fillStyle = '#fff';
        ctx.strokeStyle = '#000';
        ctx.lineWidth = 1;
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        ctx.closePath();
        ctx.fill();
      };

      function animateParticles(e) {
        const xPos = e.x;
        const yPos = e.y;
        for (let i = 0; i < 5; i++) {
          particles.push(new Particle(xPos, yPos));
        }
      }

      function animate() {
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        for (let i = 0; i < particles.length; i++) {
          particles[i].update();
          particles[i].draw();
          if (particles[i].size <= 0.2) {
            particles.splice(i, 1);
            i--;
          }
        }
        requestAnimationFrame(animate);
      }

      canvas.addEventListener('mousemove', animateParticles);
      animate();

      // Cleanup event listener when component unmounts
      return () => {
        window.removeEventListener('resize', resizeCanvas);
        canvas.removeEventListener('mousemove', animateParticles);
      };
    }
  }, []); // Empty dependency array to ensure this runs only once after the initial render

  const handleStartGesture = async () => {
    console.log("Start Button Clicked");
    try {
      const response = await axios.get("http://127.0.0.1:5000/start");
      alert(response.data.status);
      setGestureStarted(true);
    } catch (error) {
      console.error("Error starting gesture detection:", error);
    }
  };

  const handleStopGesture = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:5000/stop");
      alert(response.data.status);
      setGestureStarted(false);
    } catch (error) {
      console.error("Error stopping gesture detection:", error);
    }
  };

  return (
    <div style={{ textAlign: "center", padding: "20px", position: "relative" }}>
      <div id="background">
        <video
          autoPlay
          loop
          muted
          style={{
            position: "absolute",
            top: "0",
            left: "0",
            width: "100%",
            height: "100%",
            objectFit: "cover",
            zIndex: -1,
          }}
        >
          <source src="/videos/background.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      </div>
      <h1>Gesture Media Control</h1>
      <div>
        <button
          onClick={handleStartGesture}
          style={{
            padding: "10px 20px",
            margin: "10px",
            backgroundColor: "#4CAF50",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Start Gesture Detection
        </button>
        <button
          onClick={handleStopGesture}
          style={{
            padding: "10px 20px",
            margin: "10px",
            backgroundColor: "#f44336",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Stop Gesture Detection
        </button>
      </div>
    </div>
  );
};

export default App;
