import React from "react";
import StartButton from "./components/StartButton";
import ExitButton from "./components/ExitButton";
import VideoBox from "./components/VideoBox";
import axios from "axios";

const App = () => {

const handleStart = async () => {
  try {
    const response = await axios.get("http://127.0.0.1:5000/gesture");
    alert(`Gesture Detected: ${response.data.gesture}`);
  } catch (error) {
    console.error("Error connecting to backend:", error);
    alert("Failed to connect to backend.");
  }
};

  const handleExit = () => {
    alert("Exiting...");
    // Logic for stopping the app or video stream
  };

  return (
    <div style={appStyle}>
      <h1 style={{ textAlign: "center", color: "white" }}>Gesture Media Control</h1>
      <VideoBox />
      <div style={{ marginTop: "20px", textAlign: "center" }}>
        <StartButton onClick={handleStart} />
        <ExitButton onClick={handleExit} />
      </div>
    </div>
  );
};

const appStyle = {
  fontFamily: "Arial, sans-serif",
  color: "white",
};

export default App;
