import React, { useState } from "react";
import StartButton from "./components/StartButton";
import ExitButton from "./components/ExitButton";
import VideoBox from "./components/VideoBox";
import axios from "axios";

const App = () => {
  const [videoStarted, setVideoStarted] = useState(false);

  const handleStart = async () => {
    if (!videoStarted) {
      alert("Requesting camera permission...");
      setVideoStarted(true);
    }
  };

  const handleExit = () => {
    alert("Exiting and stopping video...");
    setVideoStarted(false);
  };

  const handleFetchGesture = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:5000/gesture");
      alert(`Gesture Detected: ${response.data.gesture}`);
    } catch (error) {
      console.error("Error connecting to backend:", error);
      alert("Failed to connect to backend.");
    }
  };

  return (
    <div style={appStyle}>
      <h1 style={{ textAlign: "center", color: "white" }}>Gesture Media Control</h1>
      <VideoBox isVideoActive={videoStarted} />
      <div style={{ marginTop: "20px", textAlign: "center" }}>
        <StartButton onClick={handleStart} />
        <ExitButton onClick={handleExit} />
      </div>
      {videoStarted && (
        <div style={{ textAlign: "center", marginTop: "20px" }}>
          <button onClick={handleFetchGesture} style={gestureButtonStyle}>
            Fetch Gesture
          </button>
        </div>
      )}
    </div>
  );
};

const appStyle = {
  fontFamily: "Arial, sans-serif",
  color: "white",
};

const gestureButtonStyle = {
  padding: "10px 20px",
  fontSize: "16px",
  backgroundColor: "#1E90FF",
  color: "white",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
};

export default App;
